    // </>,
    <>
      <div style={OVERLAY_STYLES} />
      <div style={MODAL_STYLES}>
        <h2>Terms and Conditions</h2>
        {children}
        <br />
        <div style={{ textAlign: "center" }}>
          <button className="btn-modal" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </>,

    // <>
    //   <div
